import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
    this.state={
      board :Array(9).fill("X")
    }
  }
  render() {
    const Box=this.state.board.map(box=> <div className="box">j</div>)
    return (
      <div className="App">
        <h1>Tic Tac Toe App</h1>
        <div className="board">
          {Box}
           </div>
          }
      </div>
    );
  }
}

export default App;
